OPTIONAL MATCH (a:Area {Areas: "Mirissa"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE toFloat(acc.Rating) > 8
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Rating AS Rating;

OPTIONAL MATCH (a:Area {Areas: "Nuwara Eliya"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE acc.Nearby_Places CONTAINS "Gregory Lake"
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Nearby_Places AS Nearby_Places;

OPTIONAL MATCH (a:Area {Areas: "Hikkaduwa"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE toFloat(acc.Rating) >= 8
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Rating AS Rating;

OPTIONAL MATCH (a:Area {Areas: "Ella"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE toFloat(acc.Rating) > 9
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Rating AS Rating;

OPTIONAL MATCH (a:Area {Areas: "Trincomalee"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE toFloat(acc.Rating) > 8
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Rating AS Rating;

OPTIONAL MATCH (a:Area)-[:CONSISTED_WITH]->(p:Place)
WHERE a.Population > 10000
RETURN a.Areas AS Area, collect(p.Place_To_Visit) AS PlacesToVisit;

OPTIONAL MATCH (a1:Area)-[d:HAS_DISTANCE]->(a2:Area)
WHERE a1.Areas STARTS WITH "Colombo"
RETURN a1.Areas AS FromArea, a2.Areas AS ToArea, d.Distance_in_km AS Distance;

OPTIONAL MATCH (a:Area)-[:CONSISTED_WITH]->(p:Place)
WHERE a.Population > 100000
RETURN p.Place_to_Visit, a.Areas, a.Population;

OPTIONAL MATCH (a:Area)-[:HAS_HOSPITAL]->(h:Hospital)
WHERE toString(h.Contact_Number) STARTS WITH substring(toString(h.Contact_Number), 0, 2)
RETURN a.Areas, h.Nearest_Hospital, h.Contact_Number;

OPTIONAL MATCH (a:Area)-[:LOCATED_IN]->(d:District)
WHERE a.Population > 50000
RETURN a.Areas AS Area, a.Population AS Population, d.District AS District;

OPTIONAL MATCH (a:Area {Areas: "Kandy"})-[:CONSISTED_WITH]->(p:Place)
RETURN p.Place_To_Visit AS Place, p.Activity_Type AS Activity
LIMIT 5;

OPTIONAL MATCH (a:Area {Areas: "Kandy"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE acc.Type = "Homestay"
RETURN acc.Accommodation_Place_Name AS Homestay, acc.Description AS Description, acc.Booking_Com_Booking_Link AS Booking_Link;

MATCH (a:Area {Areas: "Anuradhapura"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE acc.Description CONTAINS "outdoor swimming pool"
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Description AS Description;

OPTIONAL MATCH (a:Area {Areas: "Kataragama"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE acc.Nearby_Places CONTAINS "Yala National Park"
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Booking_Com_Booking_Link AS link, acc.Nearby_Places AS Nearby_Places;

OPTIONAL MATCH (a:Area {Areas: "Kandy"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE acc.Description CONTAINS "mountain views"
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Booking_Com_Booking_Link AS linnk, acc.Description AS Description;

OPTIONAL MATCH (a:Area {Areas: "Kataragama"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE toFloat(acc.Rating) < 7
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Rating AS Rating;

OPTIONAL MATCH (a:Area {Areas: "Arugam Bay"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE acc.Description CONTAINS "Beach"
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Description AS Description;

OPTIONAL MATCH (a:Area {Areas: "Kandy"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE acc.Description CONTAINS "spa"
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Booking_Com_Booking_Link AS booking_link, acc.Description AS Description;

OPTIONAL MATCH (a:Area {Areas: "Anuradhapura"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE acc.Booking_Com_Booking_Link IS NOT NULL
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Booking_Com_Booking_Link AS Booking_Link;
