OPTIONAL MATCH (a:Area {Areas: "Mirissa"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE toFloat(acc.Rating) > 8
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Rating AS Rating;

OPTIONAL MATCH (a:Area {Areas: "Nuwara Eliya"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE acc.Nearby_Places CONTAINS "Gregory Lake"
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Nearby_Places AS Nearby_Places;

OPTIONAL MATCH (a:Area {Areas: "Hikkaduwa"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE toFloat(acc.Rating) >= 8
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Rating AS Rating;

OPTIONAL MATCH (a:Area {Areas: "Ella"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE toFloat(acc.Rating) > 9
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Rating AS Rating;

OPTIONAL MATCH (a:Area {Areas: "Trincomalee"})-[:HAS_ACCOMADATION]->(acc:Accomadation)
WHERE toFloat(acc.Rating) > 8
RETURN acc.Accommodation_Place_Name AS Accommodation, acc.Rating AS Rating;

OPTIONAL MATCH (a:Area)-[:CONSISTED_WITH]->(p:Place)
WHERE a.Population > 10000
RETURN a.Areas AS Area, collect(p.Place_To_Visit) AS PlacesToVisit;

OPTIONAL MATCH (a1:Area)-[d:HAS_DISTANCE]->(a2:Area)
WHERE a1.Areas STARTS WITH "Colombo"
RETURN a1.Areas AS FromArea, a2.Areas AS ToArea, d.Distance_in_km AS Distance;
